schemaDescription:
DAPNote {
  data: {
      subjective: string  // Client's self-reported information, feelings, and experiences shared during the session. Include direct quotes when relevant.
      objective: string  // Therapist's clinical observations including affect, behavior, appearance, and measurable data. Focus on observable facts.
  }
  assessment: {
      clinicalImpression: string  // Professional clinical interpretation synthesizing subjective and objective data. Include diagnostic considerations.
      progress: string  // Client's progress toward established treatment goals. Note improvements, setbacks, or maintenance of gains.
      riskAssessment: string  // Assessment of safety concerns including suicidal/homicidal ideation, self-harm, and protective factors. State 'No acute safety concerns identified' if applicable.
  }
  plan: {
      interventions: string[]  // Specific therapeutic interventions used during this session and planned for future sessions.
      homework?: string  // Between-session assignments, exercises, or activities for the client to complete.
      nextSession: string  // Focus areas, goals, and topics to address in the next scheduled session.
      referrals?: string[]  // Any referrals made or recommended including psychiatry, medical, support groups, etc.
  }
  metadata: {
      sessionDate: string  // Date of the therapy session (YYYY-MM-DD format)
      sessionDuration: string  // Duration of the session (e.g., '50 minutes', '90 minutes')
      sessionType: string  // Type of session: individual, couples, family, group, telehealth, in-person
      billingCode?: string  // Suggested CPT billing code based on session type and duration (e.g., 90834, 90837, 90847)
  }
}
